mapper.select:
SELECT league_id
FROM SUMMONER_SIMPLE_HISTORY
WHERE league_id = #{leagueId}
  AND summoner_id = #{summonerId}

mapper.update:
UPDATE SUMMONER_SIMPLE_HISTORY
SET queue_type=#{queueType},
    tier=#{tier},
    rank=#{rank},
    summoner_name=#{summonerName},
    league_points=#{leaguePoints},
    wins=#{wins},
    losses=#{losses}
WHERE league_id = #{leagueId}
  AND summoner_id = #{summonerId}
